const initialState = {
    data: [],
  };

  const dataReducer = (state = initialState, action) => {
    switch (action.type) {
      case SET_DATA:
        return {
          ...state,
          data: action.payload,
          filteredData: action.payload, // Al cargar los datos, también filtramos inicialmente
        };
      case ADD_DATA:
        return {
          ...state,
          data: [...state.data, action.payload],
          filteredData: [...state.filteredData, action.payload], // Agregar los nuevos datos al estado filtrado
        };
      case FILTER_DATA:
        return {
          ...state,
          filteredData: state.data.filter(item =>
            item.nombre.toLowerCase().includes(action.payload.toLowerCase()) // Filtrar por nombre
          ),
        };
      default:
        return state;
    }
  };

  // Acciones
  export const setData = (data) => ({
    type: SET_DATA,
    payload: data,
  });

  export const addData = (newData) => ({
    type: ADD_DATA,
    payload: newData,
  });

  export const filterData = (query) => ({
    type: FILTER_DATA,
    payload: query,
  });

  export default dataReducer;
