import { configureStore } from '@reduxjs/toolkit';
import thunk from 'redux-thunk';
import dataReducer from '../features/reducer/reducer';  // Tu reducer aquí

// Crear el store usando configureStore
const store = configureStore({
  reducer: dataReducer, // Tu reducer principal
  middleware: (getDefaultMiddleware) => getDefaultMiddleware().concat(thunk), // Configurar redux-thunk si es necesario
});

export default store;
